import React,{ useState} from 'react'

import Header from './header'
import { Link, Navigate } from "react-router-dom";
// import { Context } from "../../context";
// import adi from "../images/adi.jpg"
import Post from './post';
  




// import useHistory from 'react-router-dom'

function Form() {
  // const navigate = useNavigate();
  // var { data,setData } = useContext(Context);
   const [data,setData]=useState([{
    fname:"mona",
    location:"pune",
    likes:0,
    description: "adinath",
    newimg: File,
    date:new Date()
   
}])

let name,value;
const handleChange=(e)=>{
  name=e.target.name;
  value=e.target.value

  setData({...data,[name]:value})
    


  // var newData={...data}
  // newData[e.target.name]=e.target.value
  // setData(newData)
  // console.log(newData);
 
 }




  
const saveData=async(e)=>{
  e.preventdefault();
  const {fname,location,description,newimg}=data
 
 const res= await fetch("/form",{
  method:"POST",
  headers:{
    "content-type":"application/json"
  },
  body:JSON.stringify({
    fname,
    location,
    likes:0,
    description,
    newimg,
  })
 })
//  res.then((res)=>res.json())
//  res.then((data)=>console.log(data))



 const udata=await res.json();
 if(!udata){
  window.alert("invalid data")
 }
 else{
  window.alert("post created")
 }

}


    
  return (
   
    <>
   <div>
    <Header/>
    <div>
    <form method="POST" onSubmit={saveData}>
    <div className='form'>
     <div>
     
     <input type="file" style={{margin:"5px"}} value={data.newimg} onChange={(e)=>handleChange(e)} name='newimg'/>
     </div>
     <div style={{margin:"5px"}}>
      <input type="text" placeholder='Author'   value={data.fname} onChange={(e)=>handleChange(e)} name="fname" style={{margin:"5px"}}/>
      <input type="text" placeholder='location'  value={data.location} onChange={(e)=>handleChange(e)} name='location' style={{margin:"5px"}}/>
      <div>
      <input type="text" placeholder='Description' value={data.description} onChange={(e)=>handleChange(e)} name='description'/></div>
      </div>
    
      <button onClick={handleChange}>POST</button>
    </div>
    </form>
   
    </div>
   
    </div>
    
  
    </>
  )
 
}
