import React from 'react';

// import { useContext } from "react";
// import { Context } from "../../context";
import Header from './header';
import dot from "../images/dot.svg"
import like from "../images/like.png"
import share from "../images/share.png"
import adi from "../images/adi.jpg"




export default function PostView({...props}) {
 
  // const { data,setData} = useContext(Context);
   
  return <>
  <div>
    <Header/>
   
 <br/>
  <div className='posts'>
  
    <div>
     <div style={{padding:20,float:'left'}}>{props.fname}<br/>{props.location}</div>
     <div style={{float:"right" ,padding:20}}><img  src={dot} alt="dot"/> </div>
     </div>
    
    <div>
      <img src={adi} alt="ima" width={500} height={300}/>
    </div>
    <div style={{padding:10}}>
     <img src={like} alt="like"/>
      <img  src={share} alt="share" style={{marginLeft:10}}/>
      <span style={{float:'right'}}>{props.date}</span>
    </div>
    <span style={{padding:10}}> 0 likes</span>
<p style={{padding:10}}>{props.description}</p>
  </div>
  </div>
  
  </>
}
